section.script:
const input = document.getElementById("myInput");
const dropdown = document.getElementById("customDropdown");
const icon = document.getElementById("toggleIcon");

// Function to toggle dropdown visibility
function toggleDropdown() {
dropdown.style.display = dropdown.style.display === "block" ? "none" : "block";
}

// Show dropdown when clicking on input or icon
input.addEventListener("click", toggleDropdown);
icon.addEventListener("click", toggleDropdown);

// Select value from dropdown
dropdown.addEventListener("click", function (e) {
if (e.target.dataset.value) {
input.value = e.target.dataset.value;
dropdown.style.display = "none";
}
});

// Hide dropdown when clicking outside
document.addEventListener("click", function (e) {
if (!input.contains(e.target) && !icon.contains(e.target) &&
!dropdown.contains(e.target)) {
dropdown.style.display = "none";
}
});

document.addEventListener("DOMContentLoaded", function () {
const form = document.querySelector(".onlyform");

if (!form) return; // Safety check

const phoneInputs = form.querySelectorAll("input[type='tel']");

phoneInputs.forEach(function (phoneInput) {
// Set input mode to numeric to encourage number-only input on mobile devices
phoneInput.setAttribute("inputmode", "numeric");
// Add required attribute
phoneInput.setAttribute("required", true);
// Set pattern to enforce 10 digits (for HTML5 validation)
phoneInput.setAttribute("pattern", "\\d{10}");
// Optionally, add a placeholder for guidance
phoneInput.setAttribute("placeholder", "");

phoneInput.addEventListener("input", function () {
// Remove non-digits
this.value = this.value.replace(/\D/g, '');
// Limit to 10 digits
if (this.value.length > 10) {
this.value = this.value.slice(0, 10);
}
});

// Optional: Provide real-time feedback
phoneInput.addEventListener("blur", function () {
const digitsOnly = this.value.replace(/\D/g, '');
if (digitsOnly.length > 0 && digitsOnly.length < 10) {
this.classList.add("input-error");
// Optionally show a message below the input
let errorMessage = this.nextElementSibling;
if (!errorMessage || !errorMessage.classList.contains("error-message")) {
errorMessage = document.createElement("span");
errorMessage.classList.add("error-message");
errorMessage.style.color = "red";
errorMessage.style.fontSize = "12px";
this.parentNode.insertBefore(errorMessage, this.nextSibling);
}
errorMessage.textContent = "Enter a Valid Phone Number";
} else {
this.classList.remove("input-error");
const errorMessage = this.nextElementSibling;
if (errorMessage && errorMessage.classList.contains("error-message")) {
errorMessage.textContent = "";
}
}
});
});

form.addEventListener("submit", function (e) {
let allValid = true;

phoneInputs.forEach(function (input) {
const digitsOnly = input.value.replace(/\D/g, '');
if (digitsOnly.length !== 10) {
allValid = false;
input.classList.add("input-error");
// Ensure error message is visible
let errorMessage = input.nextElementSibling;
if (!errorMessage || !errorMessage.classList.contains("error-message")) {
errorMessage = document.createElement("span");
errorMessage.classList.add("error-message");
errorMessage.style.color = "red";
errorMessage.style.fontSize = "12px";
input.parentNode.insertBefore(errorMessage, input.nextSibling);
}
errorMessage.textContent = "Phone number must be exactly 10 digits.";
} else {
input.classList.remove("input-error");
const errorMessage = input.nextElementSibling;
if (errorMessage && errorMessage.classList.contains("error-message")) {
errorMessage.textContent = "";
}
}
});

if (!allValid) {
e.preventDefault(); // Stop form submission
alert("Please enter exactly 10 digits in the phone number field.");
}
});
});

document.addEventListener("DOMContentLoaded", function () {
// Select all inputs with class="onlytext"
var textInputs = document.querySelectorAll(".onlytext");


textInputs.forEach(function (input) {
input.addEventListener("input", function () {
console.log("Input onlytext");
this.value = this.value.replace(/[0-9]/g, '');
});
});
});
